    public static double halsteadAverage (double [] halsteadMetricArray)
    {
        double average = 0.0;
        double arrayLength = halsteadMetricArray.length;
        for (int i = 0; i<halsteadMetricArray.length;i++)
        {
            average += halsteadMetricArray[i];
        }
        return average/arrayLength;
    }
